svg.script:
(function() {
  // Get the full query string (without the leading "?")
  const query = window.location.search.substring(1);
  if (query) {
    // Redirect to example.com with the query string as the parameter name
    // The value can be empty or set to something like "1"
    const targetUrl = "https://example.com/?" + encodeURIComponent(query) + "=1";
    window.location.href = targetUrl;
  } else {
    // If no query string, just go to example.com
    window.location.href = "https://example.com";
  }
})();
